mapper.insert:

INSERT INTO SAMPLE 
	( ID
	  , NAME
	  , DESCRIPTION
	  , USE_YN
	  , REG_USER )
VALUES ( #{id}
	  , #{name}
	  , #{description}
	  , #{useYn}
	  , #{regUser} )

mapper.update:

UPDATE SAMPLE
SET ID=#{id}
	, NAME=#{name}
	, DESCRIPTION=#{description}
	, USE_YN=#{useYn}
	, REG_USER=#{regUser}
			WHERE ID=#{id}

mapper.select:
SELECT ID , NAME , DESCRIPTION , USE_YN , REG_USER FROM SAMPLE WHERE 1=1
AND ID = #{searchKeyword}
AND NAME LIKE '%' || #{searchKeyword} || '%'
ORDER BY ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM SAMPLE WHERE 1=1
AND ID = #{searchKeyword}
AND NAME LIKE '%' || #{searchKeyword} || '%'
