mapper.select:
SELECT *
  FROM TBLMEMBER
 WHERE MEMID = #{memID} 
   AND MEMPWD = #{memPwd}

mapper.update:
UPDATE TBLBOARD
   SET TITLE = #{title}, CONTENT = #{content}
 WHERE IDX = #{idx}

UPDATE TBLBOARD
   SET BOARDSEQUENCE = BOARDSEQUENCE +1
 WHERE BOARDGROUP = #{boardGroup}
   AND BOARDSEQUENCE > #{boardSequence}
